import React, { useState, useEffect, useContext } from 'react';
import { getWeatherData } from '../../services/apiKeys';
import { getCityImage } from '../../services/getCityImage';

import { weatherMusicContext } from '../../App';

const Weather = ({ weatherStatus }) => {
  const [weatherdata, setWeatherData] = useState(null);
  const [city, setCity] = useState('Hamburg');
  const { changeMood } = useContext(weatherMusicContext);
  const [cityImage, setImageCity] = useState('Hamburg');

  //! Get the Weatherdata from openweather
  const getData = async () => {
    try {
      const data = await getWeatherData(city);
      setWeatherData(data);
      console.log('Data from my getDate-Funcion', data);
      changeMood(data);
    } catch (error) {
      console.log("Can't get no Data from openweather_API", error.message);
    }
  };

  // //! Get the ImageData from unsplash
  const getIamgeData = async () => {
    try {
      const data = await getCityImage(cityImage);
      setImageCity(data);
      console.log(data);
    } catch (error) {
      console.log("Can't get no Data from unsplash_API", error.message);
    }
  };

  //* Change City Name *********
  const onChangeCity = (e) => {
    setCity(e.target.value);
    setImageCity(e.target.value);
  };

  //* Button Function *********
  const onClickButton = () => {
    getData();
    getIamgeData();
  };

  // //* UseEffect / getData running *********
  // useEffect(() => {
  //   getData();
  // }, []);

  //* Date Operator ******

  const dateBuilder = () => {
    let d = new Date();
    let months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ];
    let days = [
      'Monday',
      'Tuesday',
      'Wednesday',
      'Thursday',
      'Friday',
      'Saturday',
      'Sunday',
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  //* Change Background-Image due to the weather condition

  const bgChange = () => {
    if (weatherdata === null) {
      return;
    }

    const celcius = parseFloat(weatherdata.main.temp - 273.15).toFixed(0);
    const weatherCondition = weatherdata.weather[0].main;

    //! Snow
    if (celcius <= 0) {
      return 'weather-app-snow';
    }
    //! Clowds
    if (celcius <= 5 || weatherCondition === 'Clouds') {
      return 'weather-app-clowds';
      //! Sun
    }
    //! Rain
    if (celcius >= 6 || celcius <= 16) {
      return 'weather-app-rain';
      //! Sun
    }
    if (celcius >= 17 || weatherCOn) {
      return 'weather-app-sun';
    }

    // else {
    //   return 'weather-app-warm';
    // }
  };

  //! Experiment:

  // const bgChangeWeatherCondition = () => {
  //   // if (weatherdata === null) {
  //   //   return;
  //   // }

  //   // const celcius = parseFloat(weatherdata.main.temp - 273.15).toFixed(0);
  //   // if (celcius < 0) {
  //   //   return 'weather-app-snow';
  //   // }

  //   const weatherCondition = weatherdata.weather[0].main;
  //   if (weatherCondition === 'Clouds') {
  //     return 'weather-app-clowds';
  //   }

  //   // else {
  //   //   return 'weather-app-warm';
  //   // }
  // };

  // console.log('Clowdy, rainy or sunny', weatherdata.weather[0].main);
  console.log('This is my weatherdata from Weather.js:', weatherdata);
  console.log('This is my imagedata from Weather.js', cityImage);

  return (
    <div className={`weather ${weatherStatus ? 'active-weather' : ''}`}>
      <div
        className="weather-app"
        style={{
          backgroundImage:
            'url(' + `https://source.unsplash.com/500x800/?${cityImage}` + ')',
        }}
      >
        <div className={bgChange()}>
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="City Sounds"
              onChange={onChangeCity}
            />
            <button type="button" onClick={onClickButton}>
              Search
            </button>
          </div>
          {weatherdata != null ? (
            <div className="weather-wrap">
              <div className="location-box">
                <div className="location">
                  {weatherdata.name} | {weatherdata.sys.country}
                </div>
                <div className="date">{dateBuilder()}</div>
              </div>
              <div className="weather-box">
                <div className="temp">
                  {parseFloat(weatherdata.main.temp - 273.15).toFixed(0)}Â°C
                </div>
                <div className="weather-condition">
                  {weatherdata.weather[0].main}
                </div>
              </div>
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
};

export default Weather;
