import React from 'react';

const Song = ({ currentSong, isPlaying }) => {
  const starterImgWithoutCurrentSongCoverFunction = () => {
    const starterCondition = currentSong.cover;
    if (isPlaying) {
      return starterCondition;
    }
    console.log('StarterCondition', starterCondition);
  };

  return (
    <div className="song-container">
      <img
        lat={currentSong.name}
        src={starterImgWithoutCurrentSongCoverFunction()}
        alt="coverArtwork"
      ></img>
      <h2>{currentSong.name}</h2>
      <h3>{currentSong.artist}</h3>
    </div>
  );
};

export default Song;
